def modify_num(num):
    num = num.replace('(', '')
    num = num.replace(')', '')
    num = num.replace(' ', '')
    just_num = num.replace(',', '')
    starting_point = 0
    for i in range(len(just_num)):
        if just_num[i] == '0' or just_num[i] == '০' :
            pass
        else:
            starting_point = i
            break     
    legit_number = just_num[starting_point:] 
    return legit_number  

