def lakh_semi_converter(given_number):
    given_number = modify_num(given_number) # this removes extra 0s 
    length_of_number = len(given_number)
    if length_of_number < 4:
        return_value = num_to_word(given_number)
        pass
    if length_of_number > 3 and length_of_number <6:
        hundred_range_num = given_number[-3:]
        hundred_num = f'{num_to_word(modify_num(hundred_range_num))}'

        thousend_range_num = given_number[-5:].replace(hundred_range_num, '')
        thousend_num = f'{num_to_word(modify_num(thousend_range_num))} হাজার'
        return_value = f'{thousend_num} {hundred_num}'   
    if  length_of_number > 5 and length_of_number <8:
        hundred_range_num = given_number[-3:]
        hundred_num = f'{num_to_word(modify_num(hundred_range_num))}শ'

        thousend_range_num = given_number[-5:].replace(hundred_range_num, '')
        thousend_num = f'{num_to_word(modify_num(thousend_range_num))} হাজার'
        
        lakh_range_num = given_number[-7:].replace(thousend_range_num+hundred_range_num, '')
        lakh_num = f'{num_to_word(modify_num(lakh_range_num))} লাখ' 
         
        return_value = f'{lakh_num} {thousend_num} {hundred_num}'
    return return_value    
                       
def convert_whole_number_to_word(given_number):
    length_of_number = len(given_number)
    if str(given_number[0]) == '1' or str(given_number[0]) == '১':
        if length_of_number == 4:
            sho_num = f'{num_to_word(given_number[0:2])} শ'
            j_num = num_to_word(given_number[2:4])
            return_value = f'{sho_num} {j_num}'
            return return_value
    if length_of_number < 4:
        # return_value = f'{num_to_word(given_number)}ত'
        return_value = num_to_word(given_number)
        pass
    if length_of_number > 3 and length_of_number <6:
        hundred_range_num = given_number[-3:]
        hundred_num = f'{num_to_word(modify_num(hundred_range_num))}'

        thousend_range_num = given_number[-5:].replace(hundred_range_num, '')
        thousend_num = f'{num_to_word(modify_num(thousend_range_num))} হাজার'
        return_value = f'{thousend_num} {hundred_num}'   
    if  length_of_number > 5 and length_of_number <8:
        hundred_range_num = given_number[-3:]
        hundred_num = f'{num_to_word(modify_num(hundred_range_num))}'

        thousend_range_num = given_number[-5:].replace(hundred_range_num, '')
        thousend_num = f'{num_to_word(modify_num(thousend_range_num))} হাজার'
        
        lakh_range_num = given_number[-7:].replace(thousend_range_num+hundred_range_num, '')
        lakh_num = f'{num_to_word(modify_num(lakh_range_num))} লাখ' 
         
        return_value = f'{lakh_num} {thousend_num} {hundred_num}'
    if length_of_number > 7:   
        lakh_number = lakh_semi_converter(modify_num(given_number[-7:]))
        lakh_num_range = given_number[-7:]
        corors_range = lakh_semi_converter(given_number.replace(lakh_num_range, ""))  
        return_value = f"{corors_range} কোটি {lakh_number}"
    return return_value

def convert_num_to_word(given_number):
    given_number = modify_num(given_number) # this removes extra 0s 
    if "." in given_number:
        index_of_dot = given_number.index('.')
        decimal_num_range = given_number[index_of_dot+1:]
        if decimal_num_range == "":
            given_number = given_number.replace('.', '')
            return_value = convert_whole_number_to_word(given_number)
        else:    
            decimal_num =""
            for i in range(len(decimal_num_range)):
                decimal_num += f'{num_to_word(decimal_num_range[i])} '
            whole_number = convert_whole_number_to_word(given_number.replace(given_number[index_of_dot:], ""))  
            return_value = f'{whole_number} দশমিক {decimal_num}'

    else:
        return_value = convert_whole_number_to_word(given_number)
    return return_value

